import React,{useState,useEffect} from 'react';
import 'bootstrap/dist/css/bootstrap.css';
import Header from './components/Header/Header'
import Image from './components/Image/Image'
import CardList from './components/CardList/CardList'
import axios from 'axios'

function App() {
  const[rooms,setRooms] = useState([])
  const[getRoomsTrigger, setGetRoomsTrigger] = useState(true)

  useEffect(() =>{
    if(getRoomsTrigger === true){
      getRooms()
    }
  },[getRoomsTrigger])

  const getRooms = () =>{
    axios.get('http://localhost:8080/web/data/get_rooms_by_building_id?buildingId=3')
    .then((res) =>{
      setRooms(res.data.data);
      setGetRoomsTrigger(false);
    })
  }

  const increment = (buildingId, roomId) =>{
    axios.post('http://localhost:7070/web/data/increment_room_population',{
      bulidingId:buildingId,
      roomId:roomId
    })
    .then((res) =>{
      setGetRoomsTrigger(true)
      console.log(res)
    })
  }

  const decrement = (buildingId, roomId) =>{
    axios.post('http://localhost:7070/web/data/increment_room_population',{
      bulidingId:buildingId,
      roomId:roomId
    })
    .then((res) =>{
      setGetRoomsTrigger(true)
      console.log(res)
    })
  }

  return (
    <div className="d-flex flex-column align-items-center justify-content-center">
      <Header/>
      <Image/>
      <CardList rooms={rooms} increment={increment} decrement={decrement}/>
    </div>
  );
}

export default App;
